	<?php } ?>
	<?php foreach($this->css as $css){?>
	<link rel="stylesheet" type="text/css" href="<?php echo URL ?>res/css/<?php echo $css ?>.css" />
	<?php } ?>
	<style type="text/css">
			#loading { width: 100%; height: 34px; background: url(data:image/gif;base64,) no-repeat center; }

.jumbotron{
  height:100%;
  /* background-color:rgba(66, 139, 202,0.3); */
}
.form-control{
	border-radius:17px;
}
</style>
</head>
<body style='padding-top:50px;'>
	<div class="navbar navbar-default navbar-fixed-top" role="navigation">
		<div class="container-fluid">
			<div class="navbar-header">
				<a class="brand" href="#">
					<img src='<?php echo URL ?>res/img/logo/kk8logo.png' style='max-height:40px;margin-right:10px;'/>Kinabalu Residential College
				</a>
			</div>
      </div>
    </div>
<div class="jumbotron" style='padding-top:0;margin-bottom:0;'>
<div class='container'>
<div class='row text-center'>
  <h1><b>Sign out - <?php echo $this->event['name']?></b><br></h1>
<div class='col-md-5'>
  <form role="form" action='<?php echo URL?>merit/attendance'>
    <fieldset>
	  <div class="row">
		<h2><br>Enter your matric number:</h2>
		<div class='col-md-offset-3 col-md-6'>
		  <p><input required autocomplete='on' type="text" class="form-control text-center" name="matric" style='font-weight:bold;font-size:0.8em' pattern='[A-z]{3}[0-9]{6}' title='eg. EEE130014' value='IER130003'></p>
		</div>
	  </div>
	  <input type='hidden' name='qr' value='<?php echo $this->code ?>'/>
	  <input type='submit' class="btn btn-primary btn-md" role="button" value='Sign out event'/>
    </fieldset>
	  <p>
	  <div id="loading" style='display:none'>&nbsp;</div>
	  <div class="alert alert-success col-md-offset-1 col-md-10">
		<span class="alert-link"></span> - Signed out successfully!
	  </div>
	  <div class="alert alert-danger col-md-offset-1 col-md-10">
		<span class="alert-link"></span> - Failed to sign out: <span id='failmsg'></span>
	  </div>
	  </p>
  </form>
</div>
<div class='col-md-2 text-center'>
<h1><br>OR</h1>
</div>
<div class='col-md-5'>
<h2>Scan the QR code <br>with KK8 mobile app</h2>
<img src='<?php echo URL."util/QRgenerator.php?code=".$this->code?>'/>
</div>
</div>

</div>
</div>
<script>
  $('.alert').hide();
  $(document).ready(function (){
	$('form').submit(function(){
		$('#loading').show();
		var url = $(this).attr('action');
        var post = $(this).serialize();
		$('fieldset').prop('disabled','disabled');
        $.post(url, post
		,function(data,status){
			$('.alert-link').text($('input[name="matric"]').val());
			$('.alert').finish().hide();
			$('body').append(post);
			$('#loading').hide();
			if(status == 'success')
			{
				if(data['status'] == 'success')
				{
					$('.alert-success').show().delay(3000).fadeOut(2000);
					$('input[name="matric"]').val('');
				}
				else
				{
					$('#failmsg').text(data['msg']);
					$('.alert-danger').show().delay(5000).fadeOut(3000);
				}
			}
			else
			{
				$('#failmsg').text("Could not connect to server.");
				$('.alert-danger').show().delay(5000).fadeOut(3000);
			}
			$('fieldset').prop('disabled','');
			$('input[name="matric"]').focus();
		},'json');
		return false;
	});
  });
</script>
</body>
</html>